import React, { useContext } from 'react';
import { ListGroup } from 'react-bootstrap';
import { Context } from '..';

const BrandsBar = () => {
    const { brands } = useContext(Context);
    return (
        <ListGroup>
            {brands.get().map((item) =>
                <ListGroup.Item
                    active={brands.selectedBrand === item}
                    key={item.id}
                    onClick={() => brands.setSelectedBrand(item)}> {item.name}</ListGroup.Item>
            )}
        </ListGroup >
    );
};

export default BrandsBar;
