import React from "react";
import { makeAutoObservable } from 'mobx'

class UserStore {

    isAuth: boolean = false;
    user: unknown;

    constructor() {
        makeAutoObservable(this);
    }

    setIsAuth(status: boolean) {
        this.isAuth = status;

    }
    setUser(_user: unknown) {
        this.user = _user;

    }
    getIsAuth() {
        return this.isAuth;
    }
    getUser() {
        return this.user;
    }

}

export default UserStore;
