import { $pubHost, $authHost } from ".";

export const getAllCatalogsApi = async (userId?: number) => {

    const { data } = await $pubHost.get('api/cataloge');
    return data.cataloges;
}
export const getCatalogeByUser = async (userId: number) => {

    const { data } = await $pubHost.post('api/user/login', { userId });
    localStorage.setItem("jwtHash", data.jwtHash);
    return jwtDecode(data.jwtHash);
}
