import { $pubHost, $authHost } from ".";

export const loginApi = async (name: string, password: string) => {

    const response = await $pubHost.post('api/user/login', { name, password });
    return response;
}
export const checkApi = async (name: string, password: string) => {

    const response = await $pubHost.post('api/user/auth', { name, password });
    return response;
}
